function hpbmaponload_sp_yahoo_map_1() {
	var lat = new Array(1);
	var lng = new Array(1);
	var markerwindowinfo = new Array(1);
	var markerevent = new Array(1);
	var markerlabel = new Array(1);
	var markertype = new Array(1);
	var markershow = new Array(1);
	var firstshown = new Array(1);
	var marker = new Array(1);
	lat[0] = "35.656897";
	lng[0] = "139.727184";
	markerwindowinfo[0] = "";
	markerlabel[0] = "CHI海外旅行センター";
	markerevent[0] = "click";
	markertype[0] = "";
	markershow[0] = "0";
	firstshown[0] = "1";

	var map = new Y.Map("sp-yahoo-map-1");
	map.drawMap(new Y.LatLng(35.656897, 139.727184),19,Y.LayerSetId.NORMAL);
	map.removeLayerSet(Y.LayerSetId.B1);
	map.addControl(new Y.ScaleControl());
	map.addControl(new Y.SliderZoomControlVertical());
	map.addControl(new Y.CenterMarkControl({visibleButton:false,visible:true}), new Y.ControlPosition(Y.ControlPosition.TOP_RIGHT, new Y.Size(5, 3)));
	map.addControl(new Y.LayerSetControl());
	for (var i = 0; i <= 1; i++) {
		if(firstshown[i] == 1){
			var marker;
			if(null != markertype[i] && 0 < markertype[i].length){
				var icon = new Y.Icon(markertype[i]);
				marker = new Y.Marker(new Y.LatLng(lat[i],lng[i]), {title:markerwindowinfo[i],icon:icon});
			}else{
				marker = new Y.Marker(new Y.LatLng(lat[i],lng[i]), {title:markerwindowinfo[i]});
			}
			map.addFeature(marker);
			if(markershow[i] == 1 && 0 < markerlabel[i].length){
				map.addFeature(new Y.Label(new Y.LatLng(lat[i],lng[i]), markerlabel[i]));
			}
		}
	}
	var setMapInfo = function() {
		var doc = window.document.getElementsByTagName("body")[0];
		var center = map.getCenter();
		doc.setAttribute('data-center-lat', center.lat());
		doc.setAttribute('data-center-lng', center.lng());
	};
	map.bind('moveend', setMapInfo);
	var setMapInfoFromZoom = function() {
		var doc = window.document.getElementsByTagName("body")[0];
		doc.setAttribute('data-zoom', map.getZoom());
	};
	map.bind('zoomend', setMapInfoFromZoom);
	var setMapInfoFromLayersetChange = function() {
		var doc = window.document.getElementsByTagName("body")[0];
		doc.setAttribute('data-maptype', map.getCurrentLayerSetId());
	};
	map.bind('layersetchanged', setMapInfoFromLayersetChange);
	return map;
}
